'use client';

import { useEffect, useState } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Trash2, Ban } from 'lucide-react';

export default function CollectionsModeration() {
  const [collections, setCollections] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('PUBLICADA');
  const [expandedDescriptions, setExpandedDescriptions] = useState({});
  
  useEffect(() => {
    fetchCollections();
  }, [statusFilter]);
  
  const fetchCollections = async () => {
    setLoading(true);
    try {
      const response = await fetch(`/api/admin/collections?status=${statusFilter}`);
      
      if (!response.ok) {
        setCollections([]);
        return;
      }
      
      const data = await response.json();
      
      if (Array.isArray(data)) {
        setCollections(data);
      } else {
        setCollections([]);
      }
    } catch (error) {
      console.error('Error fetching collections:', error);
      setCollections([]);
    } finally {
      setLoading(false);
    }
  };
  
  const toggleDescription = (collectionId) => {
    setExpandedDescriptions(prev => ({
      ...prev,
      [collectionId]: !prev[collectionId]
    }));
  };
  
  const truncateText = (text, maxLength = 150) => {
    if (!text || text.length <= maxLength) return text;
    return text.substring(0, maxLength) + '...';
  };
  
  const handleDelete = async (collectionId) => {
    if (!confirm('Tem certeza que deseja excluir esta cole├º├úo? Esta a├º├úo n├úo pode ser desfeita.')) {
      return;
    }
    
    try {
      const response = await fetch(`/api/admin/collections/${collectionId}`, {
        method: 'DELETE'
      });
      
      if (response.ok) {
        alert('Cole├º├úo exclu├¡da com sucesso!');
        fetchCollections();
      } else {
        alert('Erro ao excluir cole├º├úo');
      }
    } catch (error) {
      console.error('Error deleting collection:', error);
      alert('Erro ao excluir cole├º├úo');
    }
  };
  
  const handleSuspend = async (collectionId) => {
    if (!confirm('Tem certeza que deseja suspender esta cole├º├úo?')) {
      return;
    }
    
    try {
      const response = await fetch(`/api/admin/collections/${collectionId}/suspend`, {
        method: 'POST'
      });
      
      if (response.ok) {
        alert('Cole├º├úo suspensa com sucesso!');
        fetchCollections();
      } else {
        alert('Erro ao suspender cole├º├úo');
      }
    } catch (error) {
      console.error('Error suspending collection:', error);
      alert('Erro ao suspender cole├º├úo');
    }
  };
  
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <p className="text-zinc-400">Carregando cole├º├Áes...</p>
      </div>
    );
  }
  
  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-white">Cole├º├Áes</h1>
        <p className="text-zinc-400 mt-1">Gerenciar cole├º├Áes da plataforma</p>
      </div>
      
      {/* Status Tabs */}
      <div className="flex gap-2 border-b border-white/10">
        <button
          onClick={() => setStatusFilter('RASCUNHO')}
          className={`px-4 py-2 font-medium transition-colors ${
            statusFilter === 'RASCUNHO'
              ? 'text-white border-b-2 border-primary'
              : 'text-zinc-500 hover:text-white'
          }`}
        >
          Rascunhos
        </button>
        <button
          onClick={() => setStatusFilter('PUBLICADA')}
          className={`px-4 py-2 font-medium transition-colors ${
            statusFilter === 'PUBLICADA'
              ? 'text-white border-b-2 border-primary'
              : 'text-zinc-500 hover:text-white'
          }`}
        >
          Publicadas
        </button>
      </div>
      
      {/* Collections Grid */}
      {collections.length === 0 ? (
        <div className="glass-panel p-12 text-center">
          <p className="text-zinc-500">Nenhuma cole├º├úo {statusFilter.toLowerCase()} encontrada.</p>
        </div>
      ) : (
        <div className="grid gap-6">
          {collections.map((collection) => {
            const isExpanded = expandedDescriptions[collection.id];
            const description = collection.descricao || 'Sem descri├º├úo';
            const shouldTruncate = description.length > 150;
            
            return (
              <div key={collection.id} className="glass-panel p-6">
                <div className="flex gap-6">
                  {/* Thumbnail */}
                  <div className="w-48 h-32 bg-zinc-800 rounded-lg overflow-hidden flex-shrink-0">
                    {collection.imagemCapa ? (
                      <img
                        src={collection.imagemCapa}
                        alt={collection.nome}
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center text-zinc-600">
                        Sem capa
                      </div>
                    )}
                  </div>
                  
                  {/* Info */}
                  <div className="flex-1">
                    <div className="flex items-start justify-between mb-2">
                      <div>
                        <h3 className="text-xl font-bold text-white">{collection.nome}</h3>
                        <p className="text-sm text-zinc-400 mt-1">
                          por {collection.fotografo.user.name} (@{collection.fotografo.username})
                        </p>
                      </div>
                      <Badge>{collection.categoria}</Badge>
                    </div>
                    
                    <div className="mb-4">
                      <p className="text-zinc-400">
                        {isExpanded ? description : truncateText(description)}
                      </p>
                      {shouldTruncate && (
                        <button
                          onClick={() => toggleDescription(collection.id)}
                          className="text-primary text-sm hover:underline mt-1"
                        >
                          {isExpanded ? 'Ver menos' : 'Ver mais'}
                        </button>
                      )}
                    </div>
                    
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-6 text-sm text-zinc-500">
                        <span>{collection._count.fotos} fotos</span>
                        <span>R$ {collection.precoBase?.toFixed(2)}</span>
                        <span>{new Date(collection.createdAt).toLocaleDateString('pt-BR')}</span>
                      </div>
                      
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleSuspend(collection.id)}
                          className="gap-2"
                        >
                          <Ban className="w-4 h-4" />
                          Suspender
                        </Button>
                        <Button
                          variant="destructive"
                          size="sm"
                          onClick={() => handleDelete(collection.id)}
                          className="gap-2"
                        >
                          <Trash2 className="w-4 h-4" />
                          Excluir
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

