'use client';

import { useEffect, useState } from 'react';
import { Badge } from '@/components/ui/badge';
import Link from 'next/link';

export default function OrdersPage() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('');
  
  useEffect(() => {
    fetchOrders();
  }, [statusFilter]);
  
  const fetchOrders = async () => {
    setLoading(true);
    try {
      const params = statusFilter ? `?status=${statusFilter}` : '';
      const response = await fetch(`/api/admin/orders${params}`);
      
      if (!response.ok) {
        console.error('API error:', response.status);
        setOrders([]);
        return;
      }
      
      const data = await response.json();
      
      // Ensure data is an array
      if (Array.isArray(data)) {
        setOrders(data);
      } else {
        console.error('Invalid data format:', data);
        setOrders([]);
      }
    } catch (error) {
      console.error('Error fetching orders:', error);
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };
  
  const totalRevenue = Array.isArray(orders)
    ? orders
        .filter(o => o.status === 'APPROVED')
        .reduce((sum, o) => sum + parseFloat(o.total || 0), 0)
    : 0;
  
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <p className="text-zinc-400">Carregando pedidos...</p>
      </div>
    );
  }
  
  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-white">Pedidos</h1>
          <p className="text-zinc-400 mt-1">{orders.length} pedidos encontrados</p>
        </div>
        
        <div className="glass-panel px-6 py-3">
          <p className="text-sm text-zinc-400">Receita Total</p>
          <p className="text-2xl font-bold text-white">R$ {totalRevenue.toFixed(2)}</p>
        </div>
      </div>
      
      {/* Status Filter */}
      <div className="flex gap-2">
        <Badge 
          variant={!statusFilter ? 'default' : 'outline'}
          className="cursor-pointer"
          onClick={() => setStatusFilter('')}
        >
          Todos
        </Badge>
        <Badge 
          variant={statusFilter === 'PENDING' ? 'default' : 'outline'}
          className="cursor-pointer"
          onClick={() => setStatusFilter('PENDING')}
        >
          Pendentes
        </Badge>
        <Badge 
          variant={statusFilter === 'APPROVED' ? 'default' : 'outline'}
          className="cursor-pointer"
          onClick={() => setStatusFilter('APPROVED')}
        >
          Aprovados
        </Badge>
        <Badge 
          variant={statusFilter === 'CANCELLED' ? 'default' : 'outline'}
          className="cursor-pointer"
          onClick={() => setStatusFilter('CANCELLED')}
        >
          Cancelados
        </Badge>
      </div>
      
      {/* Orders Table */}
      <div className="glass-panel overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="border-b border-white/10">
              <tr>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase">ID</th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase">Cliente</th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase">Items</th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase">Total</th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase">Status</th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase">Data</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-white/5">
              {orders.map((order) => (
                <tr key={order.id} className="hover:bg-white/5 transition-colors">
                  <td className="px-6 py-4 text-sm font-mono text-zinc-400">
                    #{order.numeroSequencial || order.id.slice(0, 8)}
                  </td>
                  <td className="px-6 py-4">
                    <div>
                      <p className="font-medium text-white">{order.user.name}</p>
                      <p className="text-sm text-zinc-500">{order.user.email}</p>
                    </div>
                  </td>
                  <td className="px-6 py-4 text-zinc-400">
                    {order.items.length} foto{order.items.length !== 1 ? 's' : ''}
                  </td>
                  <td className="px-6 py-4 font-medium text-white">
                    R$ {parseFloat(order.total || 0).toFixed(2)}
                  </td>
                  <td className="px-6 py-4">
                    <Badge variant={
                      order.status === 'APPROVED' ? 'default' :
                      order.status === 'PENDING' ? 'secondary' : 'destructive'
                    }>
                      {order.status}
                    </Badge>
                  </td>
                  <td className="px-6 py-4 text-sm text-zinc-400">
                    {new Date(order.createdAt).toLocaleDateString('pt-BR')}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import Link from 'next/link';

export default function UsersPage() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState('');
  const [roleFilter, setRoleFilter] = useState('');
  
  useEffect(() => {
    fetchUsers();
  }, [roleFilter]);
  
  const fetchUsers = async () => {
    setLoading(true);
    try {
      const params = new URLSearchParams();
      if (roleFilter) params.append('role', roleFilter);
      if (search) params.append('search', search);
      
      const response = await fetch(`/api/admin/users?${params}`);
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };
  
  const handleSearch = (e) => {
    e.preventDefault();
    fetchUsers();
  };
  
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <p className="text-zinc-400">Carregando usu├írios...</p>
      </div>
    );
  }
  
  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-white">Usu├írios</h1>
          <p className="text-zinc-400 mt-1">{users.length} usu├írios cadastrados</p>
        </div>
      </div>
      
      {/* Filters */}
      <div className="glass-panel p-4">
        <form onSubmit={handleSearch} className="flex gap-4">
          <input
            type="text"
            placeholder="Buscar por nome ou email..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="flex-1 px-4 py-2 bg-white/5 border border-white/10 rounded-lg text-white placeholder:text-zinc-500 focus:outline-none focus:ring-2 focus:ring-primary"
          />
          
          <select
            value={roleFilter}
            onChange={(e) => setRoleFilter(e.target.value)}
            className="px-4 py-2 bg-white/5 border border-white/10 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-primary"
          >
            <option value="">Todos os roles</option>
            <option value="CLIENTE">Clientes</option>
            <option value="FOTOGRAFO">Fot├│grafos</option>
            <option value="ADMIN">Admins</option>
          </select>
          
          <Button type="submit">Filtrar</Button>
        </form>
      </div>
      
      {/* Users Table */}
      <div className="glass-panel overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="border-b border-white/10">
              <tr>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase tracking-wider">
                  Usu├írio
                </th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase tracking-wider">
                  Role
                </th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase tracking-wider">
                  Pedidos
                </th>
                <th className="px-6 py-4 text-left text-xs font-medium text-zinc-400 uppercase tracking-wider">
                  Cadastro
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-white/5">
              {users.map((user) => (
                <tr key={user.id} className="hover:bg-white/5 transition-colors">
                  <td className="px-6 py-4">
                    <div>
                      <p className="font-medium text-white">{user.name || 'Sem nome'}</p>
                      <p className="text-sm text-zinc-500">{user.email}</p>
                      {user.fotografo && (
                        <p className="text-xs text-primary mt-1">@{user.fotografo.username}</p>
                      )}
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <Badge variant={
                      user.role === 'ADMIN' ? 'destructive' :
                      user.role === 'FOTOGRAFO' ? 'default' : 'secondary'
                    }>
                      {user.role}
                    </Badge>
                  </td>
                  <td className="px-6 py-4 text-zinc-400">
                    {user._count.pedidos}
                  </td>
                  <td className="px-6 py-4 text-sm text-zinc-400">
                    {new Date(user.createdAt).toLocaleDateString('pt-BR')}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

