"use client";

import { useUser } from "@stackframe/stack";
import { useEffect, useRef, useState } from "react";

function UserSyncInner() {
  const user = useUser({ or: "ignore" }); // Safe usage now that it's client-only
  const syncedRef = useRef(false);

  useEffect(() => {
    if (user && !syncedRef.current) {
      syncedRef.current = true;

      const syncUser = async () => {
        try {
          await fetch("/api/users/sync", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              id: user.id,
              name: user.displayName,
              email: user.primaryEmail,
              image: user.profileImageUrl,
            }),
          });
          console.log("User synced with DB");
        } catch (error) {
          console.error("Failed to sync user:", error);
        }
      };

      syncUser();
    }
  }, [user]);

  return null;
}

export default function UserSync() {
  const [mounted, setMounted] = useState(false);
  useEffect(() => {
    setMounted(true);
  }, []);

  if (!mounted) return null;

  return <UserSyncInner />;
}

"use client";

import Link from "next/link";
import Image from "next/image";
import { usePathname } from "next/navigation";
import { Suspense, useState, useEffect } from "react";
import NavUserActions from "./NavUserActions";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { siteConfig } from "@/config/site";
import { Menu, X, ShoppingCart, Heart, Search } from "lucide-react";
import { useCart } from "@/features/cart/context/CartContext";

export default function Header() {
  const pathname = usePathname();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const { setIsCartOpen, itemCount } = useCart();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Simplified Nav Items (Removed Search/Collections if they are redundant or secondary)
  const mainNavItems = siteConfig.navItems.filter(
    (item) => !["/meus-favoritos", "/carrinho"].includes(item.href)
  );

  return (
    <>
      <header
        className={cn(
          "fixed top-0 left-0 right-0 z-50 transition-all duration-500 ease-in-out border-b border-transparent bg-white",
          scrolled
            ? "bg-black/60 backdrop-blur-xl border-white/5 py-3 shadow-[0_4px_30px_rgba(0,0,0,0.5)]"
            : "bg-transparent py-5"
        )}
      >
        <div className="container-wide flex items-center justify-between px-6 md:px-8">
          {/* LEFT: LOGO */}
          <Link
            href="/"
            className="relative z-10 flex items-center gap-2 group"
          >
            <div className="relative h-10 w-32 transition-transform duration-300 group-hover:scale-105">
              <img
                src="/logo.png"
                alt="GTClicks Logo"
                className="h-full w-full object-contain object-left"
              />
            </div>
          </Link>

          {/* CENTER: PILL NAVIGATION (Desktop Only) */}
          <nav
            className={cn(
              "absolute left-1/2 -translate-x-1/2 hidden lg:flex items-center gap-1 px-2 py-1.5 rounded-full border border-white/5 bg-white/5 backdrop-blur-md transition-all duration-500",
              scrolled
                ? "opacity-100 translate-y-0"
                : "opacity-0 -translate-y-4 pointer-events-none" // Hide nav when at top (optional, or keep always visible)
            )}
          >
            {/* Note: I decided to keep it always visible but styled differently if preferred. 
                 For now, let's make it always visible but sleeker. 
                 Reverting opacity change for usability. */}
          </nav>

          {/* Re-doing Nav to be always visible but floating */}
          <nav className="absolute left-1/2 -translate-x-1/2 hidden lg:flex items-center gap-1 px-3 py-2 rounded-full border border-white/5 bg-black/20 backdrop-blur-lg shadow-lg">
            {mainNavItems.map((item) => (
              <Link
                key={item.href}
                href={item.href}
                className={cn(
                  "text-sm font-medium px-5 py-2 rounded-full transition-all duration-300",
                  pathname === item.href
                    ? "bg-white text-black shadow-md font-bold"
                    : "text-zinc-400 hover:text-white hover:bg-white/10"
                )}
              >
                {item.label}
              </Link>
            ))}
          </nav>

          {/* RIGHT: ACTIONS */}
          <div className="flex items-center gap-3 relative z-10">
            {/* Search Trigger (Desktop) */}
            <Button
              variant="ghost"
              size="icon"
              className="hidden md:flex text-zinc-400 hover:text-white hover:bg-white/10 rounded-full"
              asChild
            >
              <Link href="/busca">
                <Search className="h-5 w-5" />
              </Link>
            </Button>

            {/* Favorites (Desktop) */}
            <Button
              variant="ghost"
              size="icon"
              className="hidden md:flex text-zinc-400 hover:text-red-500 hover:bg-white/10 rounded-full"
              asChild
            >
              <Link href="/meus-favoritos">
                <Heart className="h-5 w-5" />
              </Link>
            </Button>

            {/* Cart Trigger */}
            <Button
              variant="ghost"
              size="icon"
              className="relative text-zinc-400 hover:text-white hover:bg-white/10 rounded-full"
              onClick={() => setIsCartOpen(true)}
            >
              <ShoppingCart className="h-5 w-5" />
              {itemCount > 0 && (
                <span className="absolute top-0 right-0 h-4 w-4 rounded-full bg-primary text-[10px] font-bold flex items-center justify-center text-white border-2 border-black animate-in zoom-in">
                  {itemCount}
                </span>
              )}
            </Button>

            {/* Divider */}
            <div className="h-6 w-px bg-white/10 hidden md:block mx-1" />

            {/* User Actions (Auth) */}
            <div className="hidden md:block">
              <Suspense
                fallback={
                  <div className="w-10 h-10 rounded-full bg-white/5 animate-pulse" />
                }
              >
                <NavUserActions />
              </Suspense>
            </div>

            {/* Mobile Menu Toggle */}
            <button
              className="md:hidden p-2 text-zinc-400 hover:text-white transition-colors"
              onClick={() => setIsMobileMenuOpen(true)}
            >
              <Menu size={28} />
            </button>
          </div>
        </div>
      </header>

      {/* MOBILE MENU OVERLAY (Full Screen Glass) */}
      <div
        className={cn(
          "fixed inset-0 z-[60] bg-black/60 backdrop-blur-xl transition-all duration-300 md:hidden",
          isMobileMenuOpen
            ? "opacity-100 pointer-events-auto"
            : "opacity-0 pointer-events-none"
        )}
      >
        <div
          className={cn(
            "absolute right-0 top-0 h-full w-[80%] max-w-sm bg-zinc-950 border-l border-white/10 shadow-2xl transition-transform duration-300 flex flex-col",
            isMobileMenuOpen ? "translate-x-0" : "translate-x-full"
          )}
        >
          <div className="p-6 flex items-center justify-between border-b border-white/10">
            <span className="font-syne font-bold text-xl tracking-tight text-white">
              Menu
            </span>
            <button
              onClick={() => setIsMobileMenuOpen(false)}
              className="p-2 text-zinc-400 hover:text-white"
            >
              <X size={24} />
            </button>
          </div>

          <div className="flex-1 overflow-y-auto py-6 px-4 flex flex-col gap-2">
            {mainNavItems.map((item) => (
              <Link
                key={item.href}
                href={item.href}
                onClick={() => setIsMobileMenuOpen(false)}
                className={cn(
                  "flex items-center justify-between px-4 py-4 rounded-xl text-lg font-medium transition-all border border-transparent",
                  pathname === item.href
                    ? "bg-white/10 text-white border-white/5"
                    : "text-zinc-400 hover:text-white hover:bg-white/5"
                )}
              >
                {item.label}
              </Link>
            ))}

            <hr className="my-4 border-white/10" />

            <Link
              href="/meus-favoritos"
              onClick={() => setIsMobileMenuOpen(false)}
              className="flex items-center gap-3 px-4 py-3 text-zinc-400 hover:text-white rounded-xl hover:bg-white/5"
            >
              <Heart className="h-5 w-5" />
              Meus Favoritos
            </Link>

            <div className="mt-auto pt-8 pb-4">
              <Suspense>
                <NavUserActions mobile />
              </Suspense>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

"use client";

import Link from "next/link";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

/**
 * EmptyState - Componente padronizado para estados vazios
 * 
 * @param {Object} props
 * @param {React.ComponentType} props.icon - ├ìcone do Lucide React
 * @param {string} props.title - T├¡tulo do estado vazio
 * @param {string} props.description - Descri├º├úo opcional
 * @param {Object} props.action - A├º├úo call-to-action
 * @param {string} props.action.label - Texto do bot├úo
 * @param {Function} props.action.onClick - Callback do bot├úo
 * @param {string} props.action.href - Link do bot├úo (alternativo ao onClick)
 * @param {string} props.illustration - URL de ilustra├º├úo opcional
 * @param {'default'|'minimal'|'illustrated'} props.variant - Variante visual
 * @param {string} props.className - Classes CSS adicionais
 */
export default function EmptyState({
  icon: Icon,
  title,
  description,
  action,
  illustration,
  variant = 'default',
  className
}) {
  const isMinimal = variant === 'minimal';
  const isIllustrated = variant === 'illustrated';

  const content = (
    <div 
      className={cn(
        "flex flex-col items-center justify-center text-center",
        isMinimal ? "py-8" : "py-16",
        className
      )}
      data-testid="empty-state"
    >
      {/* Icon or Illustration */}
      {isIllustrated && illustration ? (
        <div className="mb-6">
          <img 
            src={illustration} 
            alt="" 
            className="h-48 w-48 opacity-50" 
            aria-hidden="true"
          />
        </div>
      ) : Icon && (
        <div className={cn(
          "rounded-full bg-muted flex items-center justify-center mb-6",
          isMinimal ? "h-12 w-12" : "h-16 w-16"
        )}>
          <Icon className={cn(
            "text-muted-foreground",
            isMinimal ? "h-6 w-6" : "h-8 w-8"
          )} />
        </div>
      )}

      {/* Title */}
      <h3 className={cn(
        "font-semibold text-foreground mb-2",
        isMinimal ? "text-base" : "text-xl"
      )}>
        {title}
      </h3>

      {/* Description */}
      {description && (
        <p className={cn(
          "text-muted-foreground max-w-md",
          isMinimal ? "text-sm mb-4" : "text-base mb-6"
        )}>
          {description}
        </p>
      )}

      {/* Action Button */}
      {action && (
        action.href ? (
          <Button asChild size={isMinimal ? "default" : "lg"}>
            <Link href={action.href}>
              {action.label}
            </Link>
          </Button>
        ) : (
          <Button 
            onClick={action.onClick}
            size={isMinimal ? "default" : "lg"}
          >
            {action.label}
          </Button>
        )
      )}
    </div>
  );

  return content;
}
